def check_keyboard():
	global runflag
	global breakpoint
	
	while 1:
		if runflag == 0:
			time.sleep(0.1)
			
		evt = g.getevent()
		if evt !="":
			g.show(state + " -- " + nextstate + " :: " + instr + "; regs=" + str(registers)  + " -- 'r' to toggle run mode, any key to step")
		if evt == "key q none":
			#g.setclipstr(s)
			g.exit()
		if evt == "key r none":
			runflag = 1-runflag
			breakpoint = ""
		elif evt=="key g none":
			break
		elif evt=="key b none":
			breakpoint = g.getstring("Run till breakpoint:", breakpoint)
			runflag = 1
		else:
			g.doevent(evt)
		if runflag == 1:
			break

# every state has a Z and NZ jump instruction to following states,
